import { BadgeCheckIcon, ChipIcon } from "@heroicons/react/solid";
import './Skills.css'
import { skills } from "./skills_array";

const Skills = () => {
    // Assuming you have an even number of skills, this will split the array into two.
    const halfwayThrough = Math.ceil(skills.length / 2);
    const firstHalfSkills = skills.slice(0, halfwayThrough);
    const secondHalfSkills = skills.slice(halfwayThrough);

    return (
        <section id="skills" className="container"> {/* Corrected to className */}
            <div>
                <ChipIcon className="icon mb-4" />
                <h1 className="title mb-4">
                    Skills & Technologies
                </h1>
            </div>
            <div className="skills-wrapper">
                {/* Column 1 for the first half of skills */}
                <div className="skills-column">
                    {firstHalfSkills.map((skill, index) => (
                        <div key={index} className="skill">
                            <BadgeCheckIcon className="skill-icon" />
                            <span className="skill-text">{skill}</span>
                        </div>
                    ))}
                </div>

                {/* Column 2 for the second half of skills */}
                <div className="skills-column">
                    {secondHalfSkills.map((skill, index) => (
                        <div key={index} className="skill">
                            <BadgeCheckIcon className="skill-icon" />
                            <span className="skill-text">{skill}</span>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Skills;
